import React from "react";
import { useMotionValue, useTransform, motion } from "framer-motion";

const Card = () => {
    const x = useMotionValue(0);
    const y = useMotionValue(0);
    const rotateX = useTransform(y, [-100, 100], [30, -30]);
    const rotateY = useTransform(x, [-100, 100], [-30, 30]);

    return (
        <div className="absolute top-[140px] left-[550px] fixed">
            <motion.div
                style={{ x, y, rotateX, rotateY, z: 100 }}
                drag
                dragElastic={0.20}
                dragConstraints={{ top: 0, left: 0, right: 0, bottom: 0 }}
                whileTap={{ cursor: 'grabbing' }}
                className="shadow-2xl border-[5px] border-[#014D83] rounded-[30px] w-[426px] min-h-[600px] items-center text-center justify-center bg-white">
                <div>
                    <img className="w-[200px] h-[200px] mx-[120px] py-[20px]" src="https://i.pinimg.com/originals/1f/a2/2b/1fa22befc10e3cbacd58c5b407a97997.gif" />
                    <p>Rafi Asyam</p>
                </div>
            </motion.div>
        </div>
    )
}

export default Card;
