import React from "react";
import { useMotionValue, useTransform, motion } from "framer-motion";

const Card = () => {
    const x = useMotionValue(0);
    const y = useMotionValue(0);
    const rotateX = useTransform(y, [-100, 100], [30, -30]);
    const rotateY = useTransform(x, [-100, 100], [-30, 30]);

    return (
        <div className="absolute top-[140px] left-[550px] fixed">
            <motion.div
                style={{ x, y, rotateX, rotateY, z: 100 }}
                drag
                dragElastic={0.20}
                dragConstraints={{ top: 0, left: 0, right: 0, bottom: 0 }}
                whileTap={{ cursor: 'grabbing' }}
                className="shadow-2xl border-[5px] border-[#014D83] rounded-[30px] w-[426px] min-h-[600px] items-center text-center justify-center bg-white">
                <div className="p-[30px]">
                    <img className="w-[200px] h-[200px] mx-[10px] object-cover my-[10px] rounded-[20px] border-[5px]" src="https://i.pinimg.com/564x/ce/84/d0/ce84d067aeea6941cc99d8d3da547eba.jpg" />
                    <h1 className="font-poppins font-semibold text-[20px]">Rafi Asyam</h1>
                    <p>
                        Hello everyone, I am a Web Developer/FrontEnd Dev. I really like coding displays, especially for websites.

                        and by the way I developed this website using:
                        ReactJs
                        Tailwindcss
                    </p>
                </div>
            </motion.div>
        </div>
    )
}

export default Card;
