import './App.css';
import React from "react";
import { useSpring, animated } from "react-spring";

const trans = (x, y, s) =>
  `perspective(600px) rotateX(${x}deg) rotateY(${y}deg) scale(${s})`;

const calc = (x, y) => {
  const BUFFER = 50;

  const why = -(y - window.innerHeight / 2) / BUFFER;
  const ex = (x - window.innerWidth / 2) / BUFFER;

  console.log("why", why);
  console.log("y", y);
  return [-(y / 50), x / 50, 1.1];
};

function App() {
  const [props, set] = useSpring(() => ({
    xys: [0, 0, 1],
    config: { mass: 5, tension: 350, friction: 40 }
  }));
  return (
    <section className='bg-[#0069B3] w-full h-screen fixed '>
      <div className="overflow-y-scroll ">
        <p className='h-[20px] text-slate-300 hover:text-white text-[1290%] absolute top-[-10%]  font-rubik  tracking-[-5px]'>TheScreen</p>
        <p className='h-[20px] text-slate-300 hover:text-white text-[1160%] absolute top-[10%] font-rubik  tracking-[-5px]'>ThisisText</p>
        <p className='h-[20px] text-slate-300 hover:text-white text-[1445%] absolute top-[26%] font-rubik tracking-[-5px] '>Willfull</p>
        <p className='h-[20px] text-slate-300 hover:text-white text-[1285%] absolute top-[48%]  font-rubik tracking-[-5px] '>OurScreen</p>
        <p className='h-[20px] text-slate-300 hover:text-white text-[525%] absolute top-[72%]  font-rubik tracking-[-5px] '>RememberThatYouAreAFool</p>
        <p className='h-[20px] text-slate-300 hover:text-white text-[1985%] absolute top-[71%]  font-rubik tracking-[-5px] '>MyMind</p>
      </div>
    </section>
  );
}

export default App;
